package Switchex;

public class App3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day= 7;
		
		
			
		switch(day) {
		

		case 1:
		case 2:
		case 3:
		case 4:
		case 5:
			System.out.println("weekday");
		
			break;
		
		case 6:
		case 7:
			System.out.println("weekend");
			break;
			
		default:
			System.out.println("its not a day");
		}

	}

}
