public class WhileExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     	int i=1;
     		
 		while(i<=5)
		{
			System.out.println("Welcome to loops");
			i++;
		}
	
		 System.out.println("Out of Loop"+i);
		}
		
		

}
